import InputName from './components/InputName';
import './App.css';
import { Component } from 'react';

class App extends Component {
  constructor(){  
    super();

    this.state = {
      name:'',
    }
}

handleChange = ({ target }) =>{
  const { name, value } = target; 

  this.setState = {
    [name]: value
  }
}


render(){
  return (
    <div>
      <form>
        <fieldset>
          <InputName />
        </fieldset>
      </form>
    </div>
  );
}
}

export default App;
