import InputName from './components/InputName';
import './App.css';
import { Component } from 'react';

class App extends Component {
  constructor(){  
    super();

    this.state = {
      name:'',
    }
}

handleChange = event  =>{
  let { name, value } = event.target; 

  this.setState = {
    [name]: value,
  }
}


render(){
  return (
    <div>
      <h1 className='title'>Formul√°rio Criado com React</h1>
      <form>
        <fieldset>
          <InputName onChange={this.handleChange} name={this.state.value} />
        </fieldset>
      </form>
    </div>
  );
}
}

export default App;
